export const lectureActivityControllerUpdate = <ThrowOnError extends boolean = false>(options: Options<LectureActivityControllerUpdateData, ThrowOnError>) => (options.client ?? client).patch<LectureActivityControllerUpdateResponses, unknown, ThrowOnError>({
    url: '/lecture-activity/{id}',
    ...options,
    headers: {
        'Content-Type': 'application/json',
        ...options.headers
    }
});
